'use strict';

const webhook = require('../index').Webhook;

webhook.listen(3000, function() {
    console.log('Example server listening on port 3000!');
});

const app = require('../index').Jovo;
// app.enableRequestLogging();
// app.enableResponseLogging();

// listen for post requests
webhook.post('/webhook', function(req, res) {
    app.handleRequest(req, res, handlers);
    app.execute();
});


let handlers = {

    'LAUNCH': function() {
        app.user().getAlexaShoppingList('active')
            .then((data) => {
                console.log(JSON.stringify(data, null, '\t'));

                let item = data.items[0];

                return app.user().updateListItem(data.listId, item.id, {
                    id: item.id,
                    status: item.status,
                    value: 'kaffee',
                    version: item.version,
                });
            })
            .then((data) => {
                // console.log(data);
                app.tell('App launched');
            }).catch((error) => {
                if (error.code === 'NO_USER_PERMISSION') {
                   app
                       .showAskForListPermissionCard(['read', 'write'])
                       .tell('Bitte gib uns die Berechtigung auf die Adresse zuzugreifen,');
                }
                console.log(error);
            });
    },
    'HelloWorld': function() {
        app.tell('Hello World');
    },
    'UserIntent': function() {
        app.tell('yo');
    },
};
